import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { ToastContainer } from 'react-toastify';
import { APIUrl, handleError, handleSuccess } from '../utils';

function Signin() {

    const [loginInfo, setLoginInfo] = useState({
        name: '',
        password: '' ,
    })

    const navigate = useNavigate();

    const handleChange = (e) => {
        e.preventDefault();
        const { names, value } = e.target;
        console.log(names, value);
        const copyLoginInfo = { ...loginInfo };
        copyLoginInfo[names] = value;
        setLoginInfo(copyLoginInfo);
    }
      
      
    const handleLogin = async (e) => {
        e.preventDefault();
        const { name, password } = loginInfo;
        
        if (!name || !password) {
            return handleError('name and password are required')
        }

        try {
            const url = `${APIUrl}/signin`;
            const response = await fetch(url, {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(loginInfo)
            });
            
            
            const result = await response.json();
            const { success, message, jwtToken, names, error } = result;
            if (success) {
                handleSuccess(message);
                localStorage.setItem('token', jwtToken);
                localStorage.setItem('loggedInUser', names);
                setTimeout(() => {
                    navigate('/home')
                }, 1000)
            } else if (error) {
                const details = error?.details[0].message;
                handleError(details);
            } else if (!success) {
                handleError(message);
            }
            console.log(result);
        } catch (err) {
            handleError(err);
        }
    }

    return (
        <div className='container'>
            <h1>Login</h1>
            <form onSubmit={handleLogin}>
                <div>
                    <label htmlFor='name'>name</label>
                    <input
                        onChange={handleChange}
                        type='name'
                        name='name'
                        placeholder='Enter your name...'
                        value={loginInfo.name}
                    />
                </div>
                <div>
                    <label htmlFor='password'>Password</label>
                    <input
                        onChange={handleChange}
                        type='password'
                        name='password'
                        placeholder='Enter your password...'
                        value={loginInfo.password}
                    />
                </div>
                <button type='submit'>Login</button>
                <span>Does't have an account ?
                    <Link to="/signup">Signup</Link>
                </span>
            </form>
            <ToastContainer />
        </div>
    )
}

export default Signin
